
public class Arranque {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Circulo c1 = new Circulo(0, 0, 5);
		Circulo c2 = new Circulo(1, 1, 10);
		
		c1.desplazar(10, 10);
		c2.desplazar(20, 20);
		
		if(c1.getRadio() > c2.getRadio()) System.out.println("c1 es mayor que c2");
		else if (c1.getRadio() == c2.getRadio()) System.out.println("c1 es igual que c2");
		else System.out.println("c2 es mayor que c1");
		

		if(c1.getX() == c2.getX() && c1.getY() == c2.getY()) System.out.println("Están en el mismo sitio");

		
	}

}
